import React, { useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./main.css";

function StockApp() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([]);

  const appendMessage = (text, type, options = {}) => {
    setMessages((messages) => [...messages, { text, type, ...options }]);
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    appendMessage(query, "user");
    const formData = new URLSearchParams();
    formData.append("question", query);

    try {
      const response = await fetch("http://127.0.0.1:5000/stock_data", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: formData,
      });
      const result = await response.json();
      if (result.success) {
        if (result.need_chart) {
          fetchChart(result.symbol);
        } else {
          appendMessage(`Stock Price: ${result.price}`, "system");
        }
      } else {
        appendMessage(`Error: ${result.error}`, "error");
      }
    } catch (error) {
      appendMessage(`Network Error: ${error.message}`, "error");
    }
    setQuery(""); // Reset query input after submission
  };

  const fetchChart = async (symbol) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:5000/stock_chart?symbol=${symbol}&period=6mo`
      );
      const data = await response.json();
      if (data.success) {
        // The chart is now part of the messages state, so it will render inline with other messages.
        const chartOptions = {
          chart: {
            backgroundColor: "#0d1117",
            style: {
              fontFamily: "Arial", // Set the font family if needed
            },
            height: 600, // Set height if needed, or make it responsive
          },
          title: {
            text: symbol + " Price Chart",
            style: {
              color: "#c9d1d9",
            },
          },
          xAxis: {
            type: "datetime",
            dateTimeLabelFormats: { month: "%e. %b", year: "%b" },
            gridLineColor: "#30363d",
            labels: {
              style: {
                color: "#c9d1d9",
              },
            },
          },
          yAxis: {
            title: {
              text: "Price ($)",
              style: {
                color: "#c9d1d9",
              },
            },
            gridLineColor: "#30363d",
            labels: {
              style: {
                color: "#c9d1d9",
              },
            },
          },
          legend: {
            itemStyle: {
              color: "#c9d1d9",
            },
            itemHoverStyle: {
              color: "#c9d1d9",
            },
          },
          tooltip: {
            backgroundColor: "#21262d",
            style: {
              color: "#c9d1d9",
            },
          },
          plotOptions: {
            series: {
              color: "#58a6ff",
            },
          },
          series: [
            {
              name: symbol,
              data: data.prices.map((price, index) => [
                Date.parse(data.dates[index]),
                price,
              ]),
            },
          ],
          // Add more options as needed
        };
      } else {
        appendMessage(`Error fetching chart: ${data.error}`, "error");
      }
    } catch (error) {
      appendMessage(`Network Error: ${error.message}`, "error");
    }
  };

  return (
    <div className="chat-container">
      <div className="message-display-area">
        {messages.map((msg, index) => {
          if (msg.type === "chart" && msg.chartOptions) {
            return (
              <div key={index} className="message chart-message">
                <HighchartsReact
                  highcharts={Highcharts}
                  constructorType={"stockChart"}
                  options={msg.chartOptions}
                />
              </div>
            );
          } else {
            return (
              <div key={index} className={`message ${msg.type}-message`}>
                {msg.text}
              </div>
            );
          }
        })}
      </div>
      <div className="input-area">
        <input
          type="text"
          className="text-input"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter stock query (e.g., Apple stock price)"
        />
        <button
          type="button"
          onClick={handleFormSubmit}
          className="send-button"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default StockApp;
