import React, { useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";

const ChatWindow = ({ messages, appendMessage }) => {
  const [input, setInput] = useState("");

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    appendMessage(`Query: ${input}`, "query");
    // Simulate fetching data
    // Fetch chart or other data here as needed
    appendMessage("Fetching data...", "info");
    // Simulate response
    setTimeout(() => {
      appendMessage("Data fetched. See the chart below.", "chart-info", {
        chartOptions: {
          /* Highcharts options here */
        },
      });
    }, 1000);
    setInput("");
  };

  return (
    <div className="chat-window">
      {messages.map((msg, index) =>
        msg.type === "chart" ? (
          <HighchartsReact
            highcharts={Highcharts}
            constructorType="stockChart"
            options={msg.chartOptions}
            key={index}
          />
        ) : (
          <div key={index} className={`message ${msg.type}`}>
            {msg.text}
          </div>
        )
      )}
      <form onSubmit={handleFormSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Send a message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatWindow;
