import React, { useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./main.css";

function StockApp() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([]);

  const appendMessage = (text, type, options = {}) => {
    setMessages((messages) => [...messages, { text, type, ...options }]);
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    if (query.trim() !== "") {
      appendMessage(query, "user");
      const formData = new URLSearchParams();
      formData.append("question", query);

      try {
        const response = await fetch("http://127.0.0.1:5000/stock_info", {
          method: "POST",
          headers: { "Content-Type": "application/x-www-form-urlencoded" },
          body: formData,
        });

        const result = await response.json();
        if (result.success) {
          appendMessage(result.description, "system");
          if (result.need_chart) {
            appendMessage("Stock price chart", "chart", { chartData: result });
          }
        } else {
          appendMessage(`Error: ${result.error}`, "error");
        }
      } catch (error) {
        appendMessage(`Network Error: ${error.message}`, "error");
      }
      setQuery("");
    }
  };

  const chartOptions = {
    chart: {
      type: "line",
      backgroundColor: "#11161C",
      style: { fontFamily: "Arial" },
    },
    title: {
      text: "Stock Price Chart",
      style: { color: "white" },
    },
    xAxis: {
      type: "datetime",
      dateTimeLabelFormats: { month: "%e. %b", year: "%b" },
      labels: { style: { color: "white" } },
    },
    yAxis: {
      title: {
        text: "Price ($)",
        style: { color: "white" },
      },
      labels: { style: { color: "white" } },
    },
    series: [
      {
        name: msg.chartData.symbol,
        data: msg.chartData.dates.map((date, index) => [
          new Date(date).getTime(),
          msg.chartData.prices[index],
        ]),
      },
    ],
    credits: { enabled: false }, // Disable Highcharts credits
  };

  return (
    <div className="chat-container">
      <div className="message-display-area">
        {messages.map((msg, index) => (
          <React.Fragment key={index}>
            {msg.type === "chart" && msg.chartData ? (
              <div className="message chart-message">
                <HighchartsReact
                  highcharts={Highcharts}
                  constructorType="stockChart"
                  options={{
                    ...chartOptions,
                    series: [
                      {
                        name: msg.chartData.symbol,
                        data: msg.chartData.prices.map((price, index) => [
                          new Date(msg.chartData.dates[index]).getTime(),
                          price,
                        ]),
                      },
                    ],
                  }}
                />
              </div>
            ) : (
              <div className={`message ${msg.type}-message`}>{msg.text}</div>
            )}
          </React.Fragment>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          className="text-input"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask about a stock"
        />
        <button
          type="button"
          onClick={handleFormSubmit}
          className="send-button"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default StockApp;
